{
  "version": "1.0",
   "control": "awi-form",
  "elements": [
    {
      "control": "awi-section",
      "properties": {
        "label": "Basic Info",
        "usedonactivity": [
          "DRAFT",
          "PAYMENT-DUE",
          "APPLICATION-REVIEW",
          "APPLICATION-OUTCOME",
          "CONFIGURE-ADMFEE",
          "PAYMENT-DUE-ADMFEE"
        ],
        "readonly-expression": {
          "criteria": "step",
          "operation": "In",
          "value": [
            "PAYMENT-DUE",
            "APPLICATION-REVIEW",
            "APPLICATION-OUTCOME",
            "CONFIGURE-ADMFEE",
            "PAYMENT-DUE-ADMFEE"
          ]
        }
      },
      "elements": [
        {
          "control": "awi-textbox",
          "properties": {
            "label": "Name",
            "required": true,
            "binding": "Applicant.StudentName"
          }
        },
        {
          "control": "awi-combobox",
          "properties": {
            "label": "School Year",
            "required": true,
            "binding": "Applicant.SchoolYear",
            "help-message": "Select School Year",
            "display-field": "Name",
            "dictionary": {
              "code": "DictSchoolYear",
              "domainModel": {
                "code": "{{domain_model_code}}",
                "organization": {
                  "code": "{{pubOrgCode}}"
                }
              }
            }
          }
        },
        {
          "control": "awi-textbox",
          "properties": {
            "label": "Email ID",
            "binding": "Applicant.StudentEmail",
            "pattern": "[a-z0-9._%+-]+@[a-z0-9.-]+\\.[a-z]{2,3}$"
          }
        },
        {
          "control": "awi-phone",
          "properties": {
            "label": "Mobile Phone",
            "binding": "Applicant.PhoneNumber"
          }
        }
      ]
    },
    {
      "control": "awi-section",
      "properties": {
        "label": "Education",
        "usedonactivity": [
          "DRAFT",
          "PAYMENT-DUE",
          "APPLICATION-REVIEW",
          "APPLICATION-OUTCOME",
          "CONFIGURE-ADMFEE",
          "PAYMENT-DUE-ADMFEE"
        ],
        "readonly-expression": {
          "criteria": "step",
          "operation": "In",
          "value": [
            "PAYMENT-DUE",
            "APPLICATION-REVIEW",
            "APPLICATION-OUTCOME",
            "CONFIGURE-ADMFEE",
            "PAYMENT-DUE-ADMFEE"
          ]
        }
      },
      "elements": [
        {
          "control": "awi-grid",
          "properties": {
            "binding": "Applicant.Education",
            "id": "Educationgrid",
            "label": "Education",
            "depends": "Applicant.Grades",
            "dependent-path": "Education",
            "dependant-grid-name": "Grades",
            "required": true,
            "modal-size": "medium",
            "modal-columns-layout": "single",
            "default-sort": {
              "direction": "asc",
              "binding": "CompletionYear"
            }
          },
          "elements": [
            {
              "control": "awi-gridcolumns",
              "elements": [
                {
                  "control": "awi-combobox",
                  "properties": {
                    "label": "Education Level",
                    "script-id": "SetEducationLevelSchoolName",
                    "required": true,
                    "binding": "EducationLevel",
                    "help-message": "Select Education Level",
                    "display-field": "Name",
                    "dictionary": {
                      "code": "DictEducationLevel",
                      "domainModel": {
                        "code": "{{domain_model_code}}",
                        "organization": {
                          "code": "{{pubOrgCode}}"
                        }
                      }
                    }
                  }
                },
                {
                  "control": "awi-textbox",
                  "properties": {
                    "label": "School Name",
                    "script-id": "SetEducationLevelSchoolName",
                    "binding": "SchoolName",
                    "uppercase": true
                  }
                },
                {
                  "control": "awi-textbox",
                  "properties": {
                    "label": "Completion Year",
                    "binding": "CompletionYear",
                    "uppercase": true,
                	"pattern": "^\\d{4}$"
                  }
                },
                {
                  "control": "awi-document",
                  "properties": {
                    "label": "Transcript",
                    "required": true,
                    "hidecolumn": true,
                    "binding": "Transcript"
                  }
                }
              ]
            }
          ]
        }
      ]
    },
    {
      "control": "awi-section",
      "properties": {
        "label": "Grades",
        "usedonactivity": [
          "DRAFT",
          "PAYMENT-DUE",
          "APPLICATION-REVIEW",
          "APPLICATION-OUTCOME",
          "CONFIGURE-ADMFEE",
          "PAYMENT-DUE-ADMFEE"
        ],
        "readonly-expression": {
          "criteria": "step",
          "operation": "In",
          "value": [
            "PAYMENT-DUE",
            "APPLICATION-REVIEW",
            "APPLICATION-OUTCOME",
            "CONFIGURE-ADMFEE",
            "PAYMENT-DUE-ADMFEE"
          ]
        }
      },
      "elements": [
        {
          "control": "awi-grid",
          "properties": {
            "binding": "Applicant.Grades",
            "id": "Gradesgrid",
            "label": "Grades",
            "required": true,
            "modal-size": "large",
            "modal-columns-layout": "double",
            "default-sort": {
              "direction": "asc",
              "binding": "Applicant.Grades"
            }
          },
          "elements": [
            {
              "control": "awi-gridcolumns",
              "elements": [
                {
                  "control": "awi-dropdown",
                  "properties": {
                    "label": "Education Level",
                    "required": true,
                    "wido-source-path": "Applicant.Education",
                    "display-property": "EducationLevelNSchool",
                    "binding": "EducationLevel",
                    "help-message": "Select Education Level"
                  }
                },
                {
                  "control": "awi-textbox",
                  "properties": {
                    "label": "Subject Name",
                    "required": true,
                    "binding": "SubjectName",
                    "uppercase": true
                  }
                },
                {
                  "control": "awi-combobox",
                  "properties": {
                    "label": "Grades",
                    "required": true,
                    "binding": "Grade",
                    "help-message": "Select Grade",
                    "display-field": "Name",
                    "dictionary": {
                      "code": "DictGrade",
                      "domainModel": {
                        "code": "{{domain_model_code}}",
                        "organization": {
                          "code": "{{pubOrgCode}}"
                        }
                      }
                    }
                  }
                }
              ]
            }
          ]
        }
      ]
    },
    {
      "control": "awi-section",
      "properties": {
        "label": "Service Fees",
        "usedonactivity": [
          "DRAFT",
          "CONFIGURE-ADMFEE",
          "PAYMENT-DUE-ADMFEE",
          "PAYMENT-DUE"
        ]
      },
      "elements": [
        {
          "control": "awi-pricing",
          "properties": {
            "rows": [
              {
                "code": "ADMISSION-WORKFLOW",
                "parentWorkflow": true
              }
            ]
          }
        }
      ]
    },
    {
      "control": "awi-section",
      "properties": {
        "label": "Application Decision",
        "usedonactivity": [
          "APPLICATION-REVIEW",
          "APPLICATION-OUTCOME",
          "CONFIGURE-ADMFEE",
          "PAYMENT-DUE-ADMFEE"
        ],
        "readonly-expression": {
          "criteria": "step",
          "operation": "In",
          "value": [
            "APPLICATION-OUTCOME",
            "CONFIGURE-ADMFEE",
            "PAYMENT-DUE-ADMFEE"
          ]
        }
      },
      "elements": [
        {
          "control": "awi-datepicker",
          "properties": {
            "label": "Decision Date",
            "binding": "Applicant.ApplDecisionDate",
            "show-time": false,
            "min-date": "today",
            "required": true
          }
        },
        {
          "control": "awi-combobox",
          "properties": {
            "label": "Application Decision",
            "required": true,
            "binding": "Applicant.ApplDecisionOutcome",
            "help-message": "Your Decision",
            "display-field": "Name",
            "dictionary": {
              "code": "DictApplDecisionOutcome",
              "domainModel": {
                "code": "{{domain_model_code}}",
                "organization": {
                  "code": "{{pubOrgCode}}"
                }
              }
            }
          }
        },
        {
          "control": "awi-datepicker",
          "properties": {
            "label": "Enrollment Deadline Date",
            "binding": "Applicant.EnrDeadlineDate",
            "show-time": false,
            "min-date": "today",
            "required": true
          }
        },
        {
          "control": "awi-document",
          "properties": {
            "label": "Decision Letter",
            "binding": "Applicant.ApplDecisionLetter",
            "hidden-expression": {
              "criteria": "step",
              "operation": "IsEqual",
              "value": "APPLICATION-REVIEW"
            }
          }
        }
      ]
    },
    {
      "control": "awi-section",
      "properties": {
        "label": "Acceptance",
        "usedonactivity": [
          "APPLICATION-OUTCOME",
          "CONFIGURE-ADMFEE",
          "PAYMENT-DUE-ADMFEE"
        ],
            "hidden-expression": {
              "binding-path": "Applicant.ApplDecisionOutcome.Name",
              "operation": "IsNotEqual",
              "value": "Accepted"
            },
                    "readonly-expression": {
          "criteria": "step",
          "operation": "In",
          "value": [
            "CONFIGURE-ADMFEE",
            "PAYMENT-DUE-ADMFEE"
          ]
        }
      },
      "elements": [
        {
          "control": "awi-radio",
          "properties": {
            "label": "Admission Offer",
            "binding": "Applicant.ApplAcceptance",
            "layout": "vertical",
            "items": [
              {
                "value": "Accept",
                "label": "I Accept Admission Offer"
              },
              {
                "value": "Decline",
                "label": "I Decline Admission Offer"
              }
            ],
            "required": true
          }
        }
      ]
    },
    {
      "control": "awi-section",
      "properties": {
        "label": "Admission Deposit Fee",
        "usedonactivity": [
          "APPLICATION-OUTCOME"
        ],
            "hidden-expression": {
              "binding-path": "Applicant.ApplAcceptance",
              "operation": "IsNotEqual",
              "value": "Accept"
            }
      },
      "elements": [
        {
          "control": "awi-statictext",
          "properties": {
            "value": "Be informed that test $3000 will be due upon acceptance as admission fee.",
            "preset": "3"
          }
        }
      ]
    },
    {
      "control": "awi-section",
      "properties": {
        "label": "Enrollment Info",
        "usedonactivity": [
          "ENR-INFO"
        ]
      },
      "elements": [
        {
          "control": "awi-document",
          "properties": {
            "label": "Enrollment Letter",
            "binding": "Applicant.ApplEnrollmentLetter"
          }
        },
        {
          "control": "awi-document",
          "properties": {
            "label": "Policies N Procedures Letter",
            "binding": "Applicant.ApplPoliciesnProcedures"
          }
        }
      ]
    }
  ]
}

